export const source = ({ contract, startBlock, }) => `
      address: '${contract}'
      startBlock: ${startBlock}`;
export const mapping = ({ entities, contractName, }) => `
      kind: ethereum/events
      apiVersion: 0.0.7
      language: wasm/assemblyscript
      entities:
       - ExampleEntity
      handlers:
      ${entities
    .map(entity => `
        - handler: handle${entity}
          entity: ${entity}`)
    .join(' ')}
      file: ./src/${contractName}.ts
`;
